var MyApp={Collections:{},Models:{},Settings:{},Templates:{},Utils:{},Views:{},instance:new function(){_.extend(this,Backbone.Events);},headComplete:function(){this.instance.trigger("load:complete");}};MyApp.init=function(){$("body").html(new MyApp.Views.Header().render().$el).append(new MyApp.Views.Message({text:"This is a sample message!"}).render().$el).append(new MyApp.Views.Message({text:"Please add more things!"}).render().$el);};MyApp.instance.on("load:complete",function(){MyApp.init();});MyApp.Views.Header=Backbone.View.extend({render:function(){this.$el.append(MyApp.Templates.header());return this;}});MyApp.Templates.message=Handlebars.compile('<div class="message">	<p>{{text}}</p></div>');MyApp.Templates.header=Handlebars.compile('<div class="header">	<h2>This is a Sample Implementation</h2></div>');MyApp.Views.Message=Backbone.View.extend({initialize:function(a){this.text=a.text;},render:function(){this.$el.append(MyApp.Templates.message({text:this.text}));return this;}});